// Define the favouriteMovieGenre function
function favouriteMovieGenre(genre) {
    console.log(`Your favourite movie genre is ${genre}.`);
}

// Define the favouriteFruit function
function favouriteFruit(fruit) {
    console.log(`Your favourite fruit is ${fruit}.`);
}

// Define the favouriteMode function
function favouriteMode(mode) {
    console.log(`You prefer ${mode} mode.`);
}

// Define the favouriteEdgeStyle function
function favouriteEdgeStyle(style) {
    console.log(`Your preferred edge style is ${style}.`);
}

// Call the functions
favouriteMovieGenre("fantasy");
